INSERT INTO `basic` VALUES ('276', '保护好地质工作第一手资料', '实物地质资料是地质工作中取得的第一手资料，是人类认识地球、保护和合理利用矿产资源和地质环境的基础。为进一步管好、用好实物地质资料', '', '0', '0', '2016-10-24 13:17:20', '2016-10-24 13:17:20', '0', '168', '1', '8', null, null, null, null);
