INSERT INTO `cms_article` VALUES ('236', '网络', '<p>&nbsp;（1）贯彻执行国家有关地质矿产勘查开发的方针、政策、参与制定全省地质矿产勘查事业规划、计划并组织实施。</p><p>&nbsp;（2）组织实施基础性、公益性、战略性和商业性地质矿产勘查及开发工作，开展国外地质矿产勘查，开发境外矿产资源；负责系统安全生产及质量管理。</p><p>&nbsp;（3）组织实施地勘体制改革和科技创新工作，开展多种经营和队伍经济技术、贸易合作，逐步推进地勘单位企业化改革。</p><p>&nbsp;（4）承担地质工程设计与施工，保护和治理地质环境，提供资料信息与服务。</p><p>&nbsp;（5）管理地勘经费、局机关财务、资产及所属单位的国有资产。</p><p>&nbsp;（6）负责本系统人事、劳动、职工教育培训和思想政治工作。</p><p>&nbsp;（7）完成省委、省政府及上级机关交办的其他事项。</p><p><br/></p>', '', '网络', '/157/160\\index.html', '网络', '0', '1');
INSERT INTO `cms_article` VALUES ('288', '局网站', '<p><span style=\"font-family: 宋体, Verdana; font-size: 14px; background-color: rgb(255, 255, 255);\">协助局领导组织协调机关日常工作，制定机关规章制度；负责调查研究、政务信息；负责有关综合性材料的起草、重要会议的组织和有关事项的督办；负责网络信息系统、办公自动化系统、局网站的建设与技术管理工作；负责机关值班、文秘、信访、机要、档案、保密、接待、服务、通讯、计划生育、办公用房的调整使用及局机关房产管理等工作；负责局机关的财务工作；负责管理局信息中心的日常管理工作；协调机关综合服务中心做好机关服务工作。</span></p>', '', '局网站', '/157/161/200\\index.html', '办公室', '0', '1');
INSERT INTO `cms_article` VALUES ('289', '局网站', '<p><span style=\"font-family: 宋体, Verdana; font-size: 14px; background-color: rgb(255, 255, 255);\">负责制定全局固体矿产勘查中长期发展规划和年度计划并组织实施；负责全局固体矿产勘查、物化探、区域地质调查、地质测绘和实验测试技术及质量管理；负责矿产资源勘查项目及地矿信息系统建设项目的管理和监督；推广矿产勘查工作的新理论、新技术和新方法；负责对地勘单位矿业权登记、购买、合作、转让、评估工作的监督管理；负责地勘单位矿业权经营考核工作；负责地矿科技管理工作，了解地矿科技动态，组织科学技术成果申报、评审、奖励；组织协调地勘单位矿产勘查及相关资质申报、质量认证、实验测试计量认证及知识产权申报、保护工作；负责地质资料管理工作；负责业务范围内的安全管理工作。</span></p>', '', '局网站', '/157/161/201\\index.html', '矿产勘查处', '0', '1');
